const keypad = [
  [1, 2, 3],
  [4, 5, 6],
  [7, 8, 9]
]

const demoInstructions =
`ULL
RRDDD
LURDL
UUUUD`

const realInstructions =
`



`

let pos = [1, 1]

const mutators = {
  'U': () => pos[0] = Math.max(pos[0] - 1, 0),
  'D': () => pos[0] = Math.min(pos[0] + 1, 2),
  'L': () => pos[1] = Math.max(pos[1] - 1, 0),
  'R': () => pos[1] = Math.min(pos[1] + 1, 2)
}

const getNumberForLine = line => {
  line.split('').forEach(direction => mutators[direction]())
  return keypad[pos[0]][pos[1]]
}

const getCode = data => data.split('\n').map(getNumberForLine).join('')

console.log('demo', getCode(demoInstructions))
pos = [1, 1]
console.log('4 realz', getCode(realInstructions))